  .then(() => {
    console.log('MongoDB connected successfully');
    // Start tests if NODE_ENV is 'test'
    if (process.env.NODE_ENV === 'test') {
      console.log('Running Tests...');
      setTimeout(function () {
        try {
          runner.run();
        } catch (e) {
